import React, {useContext} from 'react';
import AuthContext from "../Contexts/AuthContext";
import {useHistory} from "react-router-dom";
import Logo from "../Images/logo.png";
import "./Navbar.css";

export default function MenuAppBar() {
    const history = useHistory();
    const {user, handleLogout} = useContext(AuthContext);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const logout = () => {
        handleClose();
        handleLogout();
    };

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleLogin = () => {
        handleClose();
        history.push('/auth/login');
    };

    const handleRegister = () => {
        handleClose();
        history.push('/auth/register');
    };

    return (
        <div className="navbar">
        <img src={Logo} className="navbar-logo" alt="Gamerspace Logo"/>

        <ul className="navbar-list">
            <li className="navbar-item">
                <div className="navbar-div">Home</div>
            </li>
            <li className="navbar-item">
                <div className="navbar-div">Search</div>
            </li>
            <li className="navbar-item navbar-submenu-container">
                <div className="navbar-div">Login / Register</div>
                <ul className="navbar-submenu">
                    <li className="navbar-submenu-item">
                        <p onClick={handleLogin} className="navbar-submenu-div">Login</p>
                    </li>
                    <li className="navbar-submenu-item">
                        <p onClick={handleRegister} className="navbar-submenu-div">Register</p>
                    </li>
                    <li className="navbar-submenu-item">
                        <p onClick={logout} className="navbar-submenu-div">Logout</p>
                    </li>
                </ul>
            </li>
        </ul>
    </div>
    );
}
