import React, {useContext} from 'react';
import AuthContext from "../Contexts/AuthContext";
import {useHistory} from "react-router-dom";
import Logo from "../Images/logo.png";

export default function MenuAppBar() {
    const history = useHistory();
    const {user, handleLogout} = useContext(AuthContext);
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const logout = () => {
        handleClose();
        handleLogout();
    };

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleLogin = () => {
        handleClose();
        history.push('/auth/login');
    };

    const handleRegister = () => {
        handleClose();
        history.push('/auth/register');
    };

    return (
    <div className="navbar">
        <img src={Logo} className="navbar-logo" alt="Gamerspace Logo"/>
        <ul className="navbar-list">
            <li className="navbar-item navbar-submenu-container">

                            <div className={"navbar-link"}>{user.name}</div>
                                    <ul onClick={logout} className={"navbar-submenu-link"}>Logout</ul>

                                    <ul onClick={handleLogin} className={"navbar-submenu-link"}>Login</ul>

                                    <ul onClick={handleRegister} className={"navbar-submenu-link"}>Register</ul>

                        </li>
                    </ul>
                </div>
            )
}
