import React, {useState, useEffect} from "react";
import Button from "@material-ui/core/Button";
import {useHistory, useParams} from "react-router-dom";
import axios from "axios";
import List from "@material-ui/core/List";
import ListItemText from "@material-ui/core/ListItemText";
import ListItem from "@material-ui/core/ListItem";

export default function BrowseCategories() {
    const history = useHistory();
    const {id} = useParams();

    const [category, setCategory] = useState(null);
    const [fora, setFora] = useState([]);

    useEffect(() => {
        const getCategory = async () => {
        const response = await axios.get('/api/category/'+id);
        setCategory(response.data);
    };

    const getFora = async () => {
      const response = await axios.get('/api/forum/category/'+id);
        setFora(response.data);
    };
        getCategory();
        getFora();
    }, 
    );



    return (
        <div style={{padding: "2rem"}}>

            {category && <h3>Forum > {category.title} ></h3>}
            <div class="adminOnly">
                <button onClick={() => history.push("/forum/create/"+id)}>Create Sub Forum - ADMINISTRATORS ONLY</button>
            </div>
            <ul>
                {fora.map((forum, index) => (
                    <li button onClick={() => history.push(`/forum/${forum._id}`)}>
                        <h4>{forum.title}</h4>
                        <p>{forum.createdAt}</p>
                    </li>
                ))}
            </ul>
        </div>
    )
}
